SELECT 
		ho.order_id AS orderid, --PH order id
		ho.cust_id AS customerid, --customerid 
		orderstatusid = 7, --status 7 is accepted in Exigo, 1 is pending unsure what to use for uploads
		ho.date_ordered AS orderdate, --order date
		currencycode = 'USD', --currency USD for US, CAD for canada i believe
		warehouseid = 1, --warehouse 1 is PH warehouse 2 is YM
		shipmethodid = 3, --ask for list of shipmethods from william or cary
		ordertypeid = 6, --ask for ordertypes from william or cary
		pricetypeid = 2, --1 is retail 2 is wholesale
		SUBSTRING(s_contact,CHARINDEX(',', s_contact),LEN(s_contact) - CHARINDEX(',', s_contact)) AS FirstName,
		MiddleName = '',
		SUBSTRING(s_contact,1,CHARINDEX(',',s_contact)+1) AS LastName,
		NameSuffix = null,
		s_company AS Company,
		s_address1 as address1,
		s_address2 as address2,
		s_address3 AS address3,
		s_city AS city,
		s_state AS [State],
